import Cookies from 'js-cookie'
import React from 'react';


const axios = require('axios').default;
const URL = 'http://localhost:3000';
/*const URL = 'http://localhost:8080/api/auth';*/

const setHeaderOptions = (username, password) => {

  const options = {
    method: 'post',
    credentials: 'include',
    headers: {
      'Content-type': 'application/x-www-form-urlencoded; charset=UTF-8'
    },
    body: `username=${username}&passwd=${password}`
  }
  return options;
}

const getExpireUser = async () => {

  let valid = false;
  //con el rol
  await fetch(URL+"/verifyUser", setHeaderOptions(null, null))
  .then(response => response.json())
  .then(data => {
    valid = data.success;
  })
  alert(valid)
  return valid;
}

class AuthService extends React.Component{

  constructor(props) {
    super(props)
    this.state = null
    this.componentDidMount = this.componentDidMount.bind(this)

    this.componentDidMount()

  }

  componentDidMount() {
    // setState is called once the asynchronous call is resolved.
    getExpireUser().then(
      authorized => this.state = true
    )
  }

  login(username, password) {

    return fetch(URL+"/login", setHeaderOptions(username, password))
    .then(response => {
      if (!response.ok) {
        if (response.status === 404) {
          alert('User not found, please retry')
        }
        if (response.status === 401) {
          alert('Invalid credentials')

        }
      }
      return response
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {
        let expiry = new Date();
        expiry.setTime( new Date().getTime()+(10*60*1000));
        Cookies.set("signedin", "visited=true; max-age=30000;");
        let rol = data.success.trim();

        return rol;
      }
    })
  }

  signup(usr){
    return axios.post(`${URL}/signup`, usr)
    .then(response => response.status)
    .catch(error => {
      if (error.response){
        alert('Bad credentials')
        console.info(error.response.status);
        console.info(error.response.data);
      }else{
        alert('Server error');
        console.info(error.message);
      }
    })
  }

  updateUser(usr){
    return axios.put(`${URL}/update`, usr)
    .then(response => response.status)
    .catch(error => {
      if (error.response){
        alert('Something was wrong')
        console.info(error.response.status);
      }else{
        alert('Server error');
        console.info(error.message);
      }
    })
  }

  logout(cb) {
    this.authenticated = false;
    cb();
  }

  isAuthenticated () {
    return this.state;
  };
}

export default new AuthService();
