import React , {useState} from 'react';
import axios from 'axios';
import { Container, Row, Buttons, Button, Title } from './StyleLogin';
import { useHistory } from "react-router-dom";
import { navigate } from '@reach/router'

const Login = (props) => {

    const url = 'http://localhost:3000/login';

    const history = useHistory();

    const[username, setUsername] = useState("");
    const[password, setPassword] = useState("");

    const routeChange = () => {
        let path = '/register';
        history.push(path);
    }
    const postLogin = () => {

      var data = new FormData();

      data.append('usuario', username);
      data.append('passwd', password);

      axios.post('http://localhost:3000/login', data)
      .then(response => {
        console.log(response)

        if(response.data){
          console.log(response.data)
          let rol = response.data['role'].trim();
          let token = response.data['token'];

          if(rol === 'admin' || rol === 'auditor') {
            let path = '/admin';
            localStorage.setItem("username", username);
            history.push(path);
          }

          else if (rol === 'cliente'){
            let path = '/user';
            window.localStorage.setItem("username", username);
            window.localStorage.setItem("tipo", rol);
            history.push(path);
          }

        }
      })
      .catch(error => `Error: ${error}`);
    }
      // contacto con el servidor de react
      /*var data = new FormData();
      data.append('usuario', username);
      data.append('passwd', password);

      axios.post('http://localhost:8001/MiBanco/login', data)
      .then(response => {
        console.log(response)

        if(response.data){
          console.log(response.data)
          let rol = response.data['role'].trim();
          let token = response.data['token'];

          if(rol === 'admin' || rol === 'auditor') {
            let path = '/admin';
            localStorage.setItem("username", username);
            history.push(path);
          }

          else if (rol === 'cliente'){
            let path = '/user';
            window.localStorage.setItem("username", username);
            window.localStorage.setItem("tipo", rol);
            history.push(path);
          }

        }
      })
      .catch(error => `Error: ${error}`);*/


    return (
        <Container>
          <Title>
              <h1>Login</h1>
          </Title>
          <Row>
              <label><b>Username </b></label>
              <br /><br />
              <input type="text" name="username" onChange={event => setUsername(event.target.value)}  style={{ width:"100%" }}/>
          </Row>
          <Row>
              <label><b>Password </b></label>
              <br /><br />
              <input type="password" name="pasword" onChange={event => setPassword(event.target.value)}  style={{ width:"100%" }}/>
          </Row>
          <Buttons>
              <Button onClick={postLogin}>Login</Button>
              <Button onClick={routeChange}>Register</Button>
          </Buttons>
        </Container>
    );
}

export default Login;
