import React, { useState, useEffect } from 'react';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListSubheader from '@material-ui/core/ListSubheader';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Typography from '@material-ui/core/Typography';
import RequestService from './../../services/RequestService';
import { sobregirosCols }  from './../listas/TablesInfo';
import { makeStyles } from '@material-ui/core/styles';
import TableGrid  from './../listas/TableGrid';


const useStyles = makeStyles({
  root: {
    '& .super-app-theme--header': {
      backgroundColor: '#77ADDD',
      fontSize: 16,
      color: 'white'
    },
    '& .super-app-theme--header2': {
      backgroundColor: '#CFE7FE',
      fontSize: 16,
      color: 'black'
    },
  },
});
const ListAllUserMoves = () => {

  const[listaTrans, setListaTrans] = useState([]);
  const[listaSg, setListaSg] = useState([]);

  useEffect(() => {
    const componentDidMount = async () => {

      await RequestService.post("/clients/transactiones/getAll", null)
      .then(response => {
        if (!response.ok) {
          return
        }
        return response
      })
      .then(response => response.json())
      .then(data => {
        setListaTrans(data.success)
      })

    };

    const componentDidMountSg = async () => {

      await RequestService.post("/overdraft/getAll", null)
      .then(response => {
        if (!response.ok) {
          return
        }
        return response
      })
      .then(response => response.json())
      .then(data => {
        setListaSg(data.success)
      })
    };

    componentDidMount();
    componentDidMountSg();
  }, []);

  const classes = useStyles();

  return (
    <div style={{display: 'flex',  justifyContent:'center', alignItems:'center', width: "100%", height: "100%", flexDirection: "column"}}>
      <br /> <br />

      <div style={{maxHeight:"40vh", overflow: "scroll", width: "80vw"}}>

        <List
        component="nav"
        aria-labelledby="nested-list-subheader"
        subheader={
          <ListSubheader component="div" id="nested-list-subheader">
          Transacciones
          </ListSubheader>
        }
        >
        {listaTrans.map(user => (
          <div key={user.id} >
          <div>
          <ListItem alignItems="flex-start">

          <ListItemText
          primary={

            <React.Fragment>
            <Typography
            component="span"
            variant="body2"
            color="textPrimary"
            >
            Origen -
            </Typography>
            {" "+user.origen}
            </React.Fragment>
          }
          style={{width: '10%',  justifyContent:'flex-start'}}
          />

          <ListItemText
          primary={
            <React.Fragment>
            <Typography
            component="span"
            variant="body2"
            color="textPrimary"
            >
            Destino -
            </Typography>
            {" "+user.destino}
            </React.Fragment>
          }
          style={{width: '10%',  justifyContent:'flex-start'}}
          />

          <ListItemText
          primary={
            <React.Fragment>
            <Typography
            component="span"
            variant="body2"
            color="textPrimary"
            >
            Banco -
            </Typography>
            {" "+user.banco_destino}
            </React.Fragment>
          }
          style={{width: '10%',  justifyContent:'flex-start'}}
          />

          <ListItemText
          primary={

            <React.Fragment>
            <Typography
            component="span"
            variant="body2"
            color="textPrimary"
            >
            Saldo -
            </Typography>
            {" "+user.saldo}
            </React.Fragment>
          }
          style={{width: '10%',  justifyContent:'flex-start'}}
          />

          <ListItemText
          primary={
            <React.Fragment>
            <Typography
            component="span"
            variant="body2"
            color="textPrimary"
            >
            Estado -
            </Typography>
            {" "+user.estado}
            </React.Fragment>
          }
          style={{width: '10%',  justifyContent:'flex-start'}}
          />

          <ListItemText
          primary={
            <React.Fragment>
            <Typography
            component="span"
            variant="body2"
            color="textPrimary"
            >
            Fecha -
            </Typography>
            {" "+user.fecha}
            </React.Fragment>
          }
          style={{width: '10%',  justifyContent:'flex-start'}}
          />

          </ListItem>
          </div>
          <Divider />

          </div>
        ))}
        </List>

      </div>

      <br /> <br /> <br />

      <div style={{maxHeight:"40vh", overflow: "scroll", width: "80vw"}}>
        <TableGrid columns={sobregirosCols} rows={listaSg} pageSize={10} divStyle={{ height: '100%', width: '100%', textAlign: "center"}} className={classes.root} />

      </div>

    </div>

  );
}

export default ListAllUserMoves;
