import React , {useState} from 'react';
import axios from 'axios';
import { Container, Row, Buttons, Button, Title } from './StyleLogin';
import { useHistory } from "react-router-dom";
import { navigate } from '@reach/router'
import Cookie from "js-cookie"

export const Login = (props) => {

  const url = 'http://localhost:3000/login';

  const history = useHistory();

  const[username, setUsername] = useState("");
  const[password, setPassword] = useState("");

  const routeChange = () => {
    let path = '/register';
    history.push(path);
  }
  const postLogin = () => {

    const options = {
      method: 'post',
      credentials: 'include',
      headers: {
        'Content-type': 'application/x-www-form-urlencoded; charset=UTF-8'
      },
      body: `username=${username}&passwd=${password}`
    }

    fetch(url, options)
    .then(response => {
      if (!response.ok) {
        if (response.status === 404) {
          alert('User not found, please retry')
        }
        if (response.status === 401) {
          alert('Invalid credentials')

        }
      }
      return response
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {

        let rol = data.success.trim();

        let path = '';

        if(rol === 'admin' || rol === 'auditor') {
          path = rol;
        }
        else if(rol === 'cliente'){
          path = 'user';
        }

        history.push('/'+rol);
      }
    })
  }



  return (
    <Container>
    <Title>
    <h1>Login</h1>
    </Title>
    <Row>
    <label><b>Username </b></label>
    <br /><br />
    <input type="text" name="username" onChange={event => setUsername(event.target.value)}  style={{ width:"100%" }}/>
    </Row>
    <Row>
    <label><b>Password </b></label>
    <br /><br />
    <input type="password" name="pasword" onChange={event => setPassword(event.target.value)}  style={{ width:"100%" }}/>
    </Row>
    <Buttons>
    <Button onClick={postLogin}>Login</Button>
    <Button onClick={routeChange}>Register</Button>
    </Buttons>
    </Container>
  );
}

export default Login;
