import Cookies from 'js-cookie'


const axios = require('axios').default;
const URL = 'http://localhost:3000';
/*const URL = 'http://localhost:8080/api/auth';*/

const setHeaderOptions = (username, password) => {

  const options = {
    method: 'post',
    credentials: 'include',
    headers: {
      'Content-type': 'application/x-www-form-urlencoded; charset=UTF-8'
    },
    body: `username=${username}&passwd=${password}`
  }

  return options;

}


class AuthService {
  constructor() {
    const user = Cookies.get('signedin')
    this.authenticated = !!user;
  }

  login(username, password) {

    return fetch(URL+"/login", setHeaderOptions(username, password))
    .then(response => {
      if (!response.ok) {
        if (response.status === 404) {
          alert('User not found, please retry')
        }
        if (response.status === 401) {
          alert('Invalid credentials')

        }
      }
      return response
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {
        console,log(data.success)
        let rol = data.success.trim();

        return rol;
      }
    })
  }

  signup(usr){
    return axios.post(`${URL}/signup`, usr)
        .then(response => response.status)
        .catch(error => {
          if (error.response){
            alert('Bad credentials')
            console.info(error.response.status);
            console.info(error.response.data);
          }else{
            alert('Server error');
            console.info(error.message);
          }
        })
  }

  updateUser(usr){
    return axios.put(`${URL}/update`, usr)
        .then(response => response.status)
        .catch(error => {
          if (error.response){
            alert('Something was wrong')
            console.info(error.response.status);
          }else{
            alert('Server error');
            console.info(error.message);
          }
        })
  }

  logout(cb) {
    this.authenticated = false;
    cb();
  }

  isAuthenticated() {
    return this.authenticated;
  }
}

export default new AuthService();
