import React, { useState } from 'react';
import { Container, Row, Button, Title } from './StyleRegister';
import { useHistory } from 'react-router-dom';
import RequestService from './../../services/RequestService';

const Register = () => {

  const [document, setDocument] = useState("");

  const postRegUser = props => {
    alert(document)

    let reqBody = `documento=${document}`

    RequestService.post("/signup/valid", reqBody)
    .then(response => {
      if (!response.ok) {
        alert("Datos inválidos")
        window.location.reload(false);
        return
      }
      return response
    })
    .then(response => response.json())
    .then(data => {
      alert(JSON.stringify(data.success))
      window.location.reload(false);
    })
  }

  return (
    <Container>
      <Title>
        <h1>Register</h1>
      </Title>
      <Row>
        <label>Documento: </label>
        <input type="text" maxLength="10" name="documento" onChange={event => setDocument(event.target.value)}/>
      </Row>
      <Button type="submit" onClick={postRegUser}>Enter</Button>
    </Container>
  );
}

export default Register;
